import React from 'react';
import {connect} from 'react-redux';

import {setPage, openPopout, closePopout} from "../../store/router/actions";

import {
    Panel,
    PanelHeader,
    Group,
    RichCell,
    Avatar,
    Link,
    Button,
    CellButton,
    Div,
    Footer,
    Text,
    Spacing
} from "@vkontakte/vkui";

import {
  Icon12Verified,
  Icon28HelpOutline,
  Icon28MasksOutline,
  Icon28MessageOutline
} from '@vkontakte/icons';

class InfoPanel extends React.Component {
  constructor(props) {
    super(props);

    this.state = {

    };
  }

  render() {
    const {id} = this.props;

    return (
      <Panel id={id}>
        <PanelHeader separator={true}>Информация</PanelHeader>
        <Group>
          <Div>
            <Text weight="regular">
              Привет, создатель масок! Этот сервис создан командой SkyReglis Studio для помощи в создании масок.
            </Text>
          </Div>
          <RichCell
